import React, { useState } from 'react'
import {Button, Modal} from 'react-bootstrap'

// import 'bootstrap/dist/css/bootstrap.min.css';

const toggle = (e) => {
    const current = e.currentTarget.style.backgroundColor;
    e.currentTarget.style.backgroundColor = current === 'green' ? '' : 'green';
    if(current === 'green') {
        const res = window.confirm('Can you take the whole delivery?');
        console.log(res);
    }
}

const Contacts = ({ contacts }) => {

    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);  

    return (
        <div>
            <center><h1>Deliveries</h1></center>
            {contacts.map((contact) => (
            <div className="card" key={contact.id} onClick={handleShow}>
                <div className="card-body">
                <h5 className="card-title">{contact.name}</h5>
                <h6 className="card-subtitle mb-2 text-muted">{contact.email}</h6>
                <p className="card-text">{contact.company.catchPhrase}</p>
                </div>
            </div>
            ))}

            <Modal show={show} onHide={handleClose} animation={false} centered>
                <Modal.Header closeButton>
                    <Modal.Title>Modal heading</Modal.Title>
                </Modal.Header>
                <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleClose}>
                        Save Changes
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    )
};

export default Contacts;
