import React, { Component } from 'react'
import {Button, Modal} from 'react-bootstrap'

class Deliveries extends Component {
    state = {
        show: false
    }

    render() {
        const { deliveries } = this.props;
        return (
            <>
                <div>
                    {deliveries.map((delivery) => (
                        <Delivery key={delivery.id} delivery={delivery} />
                    ))}
                </div>
            </>
      );
    }
}

class Delivery extends Component {
    state = { show: false }

    handleClick({ currentTarget }, id) {
        this.setState({ show: true, deliveryDOMNode: currentTarget, deliveryId: id })
    }

    render() {
        const { delivery } = this.props;
        return (
            <>
                <Modal show={this.state.show} onHide={() => this.setState({show: false})} animation={false} centered>
                    <Modal.Header closeButton>
                        <Modal.Title>Fulfill Delivery ({this.state.id})</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>Can you fulfill this entire delivery?</Modal.Body>
                    <Modal.Footer>
                        <Button variant="warning" onClick={null}>
                            Part of it
                        </Button>
                        <Button variant="primary" onClick={null}>
                            All of it
                        </Button>
                    </Modal.Footer>
                </Modal>

                <div className="card" key={delivery.id} onClick={(e) => this.handleClick(e, delivery.id)}>
                    <div className="card-body">
                        <h5 className="card-title">{delivery.name}</h5>
                        <h6 className="card-subtitle mb-2 text-muted">{delivery.email}</h6>
                        <p className="card-text">{delivery.company.catchPhrase}</p>
                    </div>
                </div>
            </>
      );
    }
}

class ModalWang extends Component {
    state = { show: true }
    // handleShow() {
    //     this.props.show = true;
    // }

    // var handleShow = (e) => {
    //     console.log(`setting selectedDelivery ${window.selectedDelivery} to ${e}`)
    //     window.selectedDelivery = e.currentTarget;
    //     console.log(window.selectedDelivery);
    //     setShow(true)
    // };

    render() {
        return (
            <Modal show={this.props.show && this.state.show} onHide={() => this.setState({show: false})} animation={false} centered>
                <Modal.Header closeButton>
                    <Modal.Title>Fulfill Delivery</Modal.Title>
                </Modal.Header>
                <Modal.Body>Can you fulfill this entire delivery?</Modal.Body>
                <Modal.Footer>
                    <Button variant="warning" onClick={null}>
                        Part of it
                    </Button>
                    <Button variant="primary" onClick={null}>
                        All of it
                    </Button>
                </Modal.Footer>
            </Modal>
        );
    }
}

export default Deliveries;
