import React, { useState } from 'react'
import {Button, Modal} from 'react-bootstrap'
// import 'bootstrap/dist/css/bootstrap.min.css';

const Contacts = ({ contacts }) => {

    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const claimComplete = (e) => {
        console.log('complete');
        handleClose();
    };
    const claimPartial = (e) => {
        console.log(e);
        console.log(e.currentTarget);
        claim()
            .then((data) => {
                e.currentTarget.style.backgroundColor = 'yellow';
                handleClose();
            })
            .catch(console.log);
    };

    const claim = () => {
        return fetch('http://jsonplaceholder.typicode.com/users')
        .then(res => res.json())
        .then((data) => {
          console.log('fake done')
          return data;
        })
        .catch(console.log)
    };

    return (
        <div>
            <center><h1>Deliveries</h1></center>
            {contacts.map((contact) => (
            <div className="card" key={contact.id} onClick={handleShow}>
                <div className="card-body">
                <h5 className="card-title">{contact.name}</h5>
                <h6 className="card-subtitle mb-2 text-muted">{contact.email}</h6>
                <p className="card-text">{contact.company.catchPhrase}</p>
                </div>
            </div>
            ))}

            <Modal show={show} onHide={handleClose} animation={false} centered>
                <Modal.Header closeButton>
                    <Modal.Title>Fulfill Delivery</Modal.Title>
                </Modal.Header>
                <Modal.Body>Can you fulfill this entire delivery?</Modal.Body>
                <Modal.Footer>
                    <Button variant="warning" onClick={claimPartial}>
                        Part of it
                    </Button>
                    <Button variant="primary" onClick={claimComplete}>
                        All of it
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    )
};

export default Contacts;
