import Button from 'react-bootstrap/Button'
import Modal from 'react-bootstrap/ModalDialog'

const toggle = (e) => {
    const current = e.currentTarget.style.backgroundColor;
    e.currentTarget.style.backgroundColor = current === 'green' ? '' : 'green';
    if(current === 'green') {
        const res = window.confirm('Can you take the whole delivery?');
        console.log(res);
    }
}

